from enum import Enumclass Light:    class State(Enum):        RED = 1        GREEN = 2    def __init__(self, id: int, duration: float, junction: float, state: State = State.RED):        self.state: Light.State = state        self.duration: float = duration        self.id = id        self.junction = junction        self.counter = duration        def _decrement_counter(self, val = 1):        self.counter -= val    def _reset_counter(self):        self.counter = self.duration    def _toggle(self):        if self.state == Light.State.RED:            self.state = Light.State.GREEN        else:            self.state = Light.State.RED                self._reset_counter()    def step(self, dt):        self._decrement_counter(dt)        if self.counter == 0:            self._toggle()